                  <Text style={styles.welcomeItem}>ğŸ“… Historical data analysis with date ranges</Text>
                  <Text style={styles.welcomeItem}>ğŸ“ˆ Peak & low consumption detection</Text>
                </View>
                <Text style={styles.welcomeTip}>
                  Try asking: "What is the current power consumption?" or use the ğŸ“… Date Analysis button above!
                </Text>
              </View>
            )}
            
            {messages.map((msg, index) => renderMessage(msg, index))}
            
            {isLoading && (
              <View style={[styles.messageContainer]}>
                <View style={[styles.messageBubble, styles.aiBubble]}>
                  <Text style={styles.messageIcon}>ğŸ¤–</Text>
                  <View style={styles.loadingContainer}>
                    <ActivityIndicator size="small" color="#6366f1" />
                    <Text style={styles.loadingText}>Thinking...</Text>
                  </View>
                </View>
              </View>
            )}
          </ScrollView>

          {/* Input Container */}
          <View style={styles.inputContainer}>
            <TextInput
              style={styles.input}
              value={inputMessage}
              onChangeText={setInputMessage}
              placeholder="Ask about your microgrid data..."
              placeholderTextColor="#94a3b8"
              multiline
              editable={!isLoading}
            />
            <TouchableOpacity 
              style={[styles.sendButton, (!inputMessage.trim() || isLoading) && styles.sendButtonDisabled]}
              onPress={sendMessage}
              disabled={isLoading || !inputMessage.trim()}
            >
              <Text style={styles.sendButtonText}>{isLoading ? 'â³' : 'ğŸ“¤'}</Text>
            </TouchableOpacity>
          </View>
        </KeyboardAvoidingView>
      </LinearGradient>
    </SafeAreaView>
  );
};

