import React, { useState, useEffect } from 'react';
import {
  View,
  Text,
  StyleSheet,
  ScrollView,
  TouchableOpacity,
  Dimensions,
  Platform,
  TextInput,
  Keyboard,
} from 'react-native';
import { Ionicons } from '@expo/vector-icons';
import { LinearGradient } from 'expo-linear-gradient';
import WebSocketService from '../services/WebSocketService';

const { width } = Dimensions.get('window');

// Storage helper for cross-platform compatibility
const Storage = {
  getItem: async (key) => {
    if (Platform.OS === 'web') {
      return localStorage.getItem(key);
    } else {
      const AsyncStorage = require('@react-native-async-storage/async-storage').default;
      return await AsyncStorage.getItem(key);
    }
  },
  setItem: async (key, value) => {
    if (Platform.OS === 'web') {
      localStorage.setItem(key, value);
    } else {
      const AsyncStorage = require('@react-native-async-storage/async-storage').default;
      await AsyncStorage.setItem(key, value);
    }
  }
};

const HomeScreen = () => {
  const isSmallScreen = width < 400;
  const isTablet = width > 768;
  
  // State for meter data from WebSocket (Live Meter Page)
  const [meterData, setMeterData] = useState({
    1: { energy: 50, power: 1200 },
    2: { energy: 45, power: 1080 },
    3: { energy: 62, power: 1435 },
    4: { energy: 55, power: 1254 },
  });
  const [connectionStatus, setConnectionStatus] = useState('Disconnected');
  
  // State for energy tracking (in Units - 1 kWh = 1 Unit)
  const [dailyUnits, setDailyUnits] = useState(0); // Daily energy in Units
  const [yearlyUnits, setYearlyUnits] = useState(0); // Yearly energy in Units
  const [lastUpdateDate, setLastUpdateDate] = useState(null);
  
  // State for user input product profit calculation
  const [userInputUnits, setUserInputUnits] = useState('');
  const [confirmedUserUnits, setConfirmedUserUnits] = useState(0);

  // Rate per Unit (₹6 per Unit/kWh)
  const ratePerUnit = 6;

  // Connect to WebSocket to get live meter data
  useEffect(() => {
    WebSocketService.connect();
    setConnectionStatus('Connecting...');

    const handleWebSocketMessage = (message) => {
      if (message.data && message.meterId) {
        const meterDataReceived = message.data;
        const meterId = message.meterId;

        setMeterData(prevData => ({
          ...prevData,
          [meterId]: {
            ...prevData[meterId],
            ...meterDataReceived
          }
        }));
      }
    };

    const handleConnectionStatus = (status) => {
      setConnectionStatus(status);
    };

    WebSocketService.addMessageListener(handleWebSocketMessage);
    WebSocketService.addConnectionStatusListener(handleConnectionStatus);

    return () => {
      WebSocketService.removeMessageListener(handleWebSocketMessage);
      WebSocketService.removeConnectionStatusListener(handleConnectionStatus);
    };
  }, []);

  // Calculate total energy (Units) from all meters
  const calculateTotalMeterUnits = () => {
    let totalUnits = 0;
    Object.values(meterData).forEach(meter => {
      if (meter && meter.energy !== undefined) {
        totalUnits += meter.energy; // 1 kWh = 1 Unit
      }
    });
    return totalUnits;
  };

  // Load saved energy data on mount
  useEffect(() => {
    const loadEnergyData = async () => {
      try {
        const savedData = await Storage.getItem('@home_energy_data_v3');
        if (savedData) {
          const data = JSON.parse(savedData);
          const today = new Date().toDateString();
          
          if (data.lastUpdateDate !== today) {
            // New day - use meter data
            const meterUnits = calculateTotalMeterUnits();
            setDailyUnits(meterUnits);
            setLastUpdateDate(today);
          } else {
            setDailyUnits(data.dailyUnits || calculateTotalMeterUnits());
            setLastUpdateDate(data.lastUpdateDate);
          }
          setYearlyUnits(data.yearlyUnits || 0);
          setConfirmedUserUnits(data.confirmedUserUnits || 0);
        } else {
          setLastUpdateDate(new Date().toDateString());
          const meterUnits = calculateTotalMeterUnits();
          setDailyUnits(meterUnits);
          setYearlyUnits(meterUnits * 365);
        }
      } catch (error) {
        console.log('Error loading energy data:', error);
        // Set default values on error
        const meterUnits = calculateTotalMeterUnits();
        setDailyUnits(meterUnits);
        setYearlyUnits(meterUnits * 365);
        setLastUpdateDate(new Date().toDateString());
      }
    };
    loadEnergyData();
  }, []);

  // Update daily and yearly units from meter data
  useEffect(() => {
    const meterUnits = calculateTotalMeterUnits();
    if (meterUnits > 0) {
      setDailyUnits(meterUnits);
      setYearlyUnits(meterUnits * 365); // Estimated yearly based on daily
    }
  }, [meterData]);

  // Save energy data whenever it changes
  useEffect(() => {
    const saveEnergyData = async () => {
      try {
        const data = {
          dailyUnits,
          yearlyUnits,
          lastUpdateDate: lastUpdateDate || new Date().toDateString(),
          confirmedUserUnits,
        };
        await Storage.setItem('@home_energy_data_v3', JSON.stringify(data));
      } catch (error) {
        console.log('Error saving energy data:', error);
      }
    };
    if (lastUpdateDate) {
      saveEnergyData();
    }
  }, [dailyUnits, yearlyUnits, lastUpdateDate, confirmedUserUnits]);

  // Calculate money (₹6 per Unit)
  const dailyMoney = dailyUnits * ratePerUnit; // Daily Units × ₹6
  const yearlyMoney = yearlyUnits * ratePerUnit; // Yearly Units × ₹6
  
  // Product profit calculation
  const userInputMoney = confirmedUserUnits * ratePerUnit; // User input units × ₹6
  const productProfit = yearlyMoney - userInputMoney; // Live Yearly Money - User Input Money

  // Handle user input - auto calculate on input change
  const handleUserInputChange = (value) => {
    setUserInputUnits(value);
    const unitsValue = parseFloat(value);
    if (!isNaN(unitsValue) && unitsValue >= 0) {
      setConfirmedUserUnits(unitsValue);
    } else {
      setConfirmedUserUnits(0);
    }
  };

  // Format number with commas for Indian number system
  const formatIndianNumber = (num) => {
    return num.toLocaleString('en-IN', { maximumFractionDigits: 2 });
  };
  
  const getCardWidth = () => {
    if (isSmallScreen) return '100%';
    if (isTablet) return '23%';
    return '47%';
  };

  // Get current power from meters
  const getTotalPower = () => {
    let totalPower = 0;
    Object.values(meterData).forEach(meter => {
      if (meter && meter.power !== undefined) {
        totalPower += meter.power;
      }
    });
    return totalPower;
  };

  // Count online meters
  const getOnlineMeters = () => {
    return Object.values(meterData).filter(meter => meter !== null).length;
  };

  const quickStats = [
    { title: 'Total Units', value: formatIndianNumber(dailyUnits), unit: 'Units', icon: 'flash', gradient: ['#6366f1', '#8b5cf6'], bgColor: '#eef2ff' },
    { title: 'Active Meters', value: getOnlineMeters().toString(), unit: 'Online', icon: 'speedometer', gradient: ['#10b981', '#34d399'], bgColor: '#ecfdf5' },
    { title: 'Total Power', value: formatIndianNumber(getTotalPower()), unit: 'W', icon: 'sunny', gradient: ['#f59e0b', '#fbbf24'], bgColor: '#fffbeb' },
    { title: 'Connection', value: connectionStatus === 'Connected' ? 'Live' : 'Offline', unit: '', icon: 'wifi', gradient: ['#06b6d4', '#22d3ee'], bgColor: '#ecfeff' },
  ];

  const systemStatus = [
    { name: 'Meter 1', status: 'Online', value: '1200 W', icon: 'speedometer', color: '#6366f1' },
    { name: 'Meter 2', status: 'Online', value: '850 W', icon: 'speedometer', color: '#8b5cf6' },
    { name: 'Solar Panel', status: 'Online', value: '850 W', icon: 'sunny', color: '#f59e0b' },
    { name: 'Wind Turbine', status: 'Online', value: '320 W', icon: 'leaf', color: '#06b6d4' },
  ];

  const alerts = [
    { type: 'warning', icon: 'warning', color: '#f59e0b', bgColor: '#fef3c7', text: 'Power factor low on Meter 2', time: '2 hours ago' },
    { type: 'info', icon: 'information-circle', color: '#3b82f6', bgColor: '#dbeafe', text: 'Maintenance scheduled for tomorrow', time: '1 day ago' },
  ];

  return (
    <View style={styles.container}>
      {/* Modern Header with Gradient */}
      <LinearGradient
        colors={['#0f172a', '#1e293b']}
        style={styles.header}
        start={{ x: 0, y: 0 }}
        end={{ x: 1, y: 1 }}
      >
        <View style={styles.headerContent}>
          <View style={styles.headerTextContainer}>
            <Text style={styles.greeting}>Good Morning! ☀️</Text>
            <Text style={styles.welcomeText}>Energy Dashboard</Text>
            <Text style={styles.subText}>Monitor your microgrid in real-time</Text>
          </View>
          <TouchableOpacity style={styles.profileButton}>
            <LinearGradient
              colors={['#6366f1', '#8b5cf6']}
              style={styles.profileGradient}
            >
              <Ionicons name="person" size={20} color="#ffffff" />
            </LinearGradient>
          </TouchableOpacity>
        </View>

        {/* Energy Summary Card */}
        <View style={styles.summaryCard}>
          <View style={styles.summaryRow}>
            <View style={styles.summaryItem}>
              <Text style={styles.summaryLabel}>Daily Energy</Text>
              <Text style={styles.summaryValue}>{formatIndianNumber(dailyEnergy)} kWh</Text>
            </View>
            <View style={styles.summaryDivider} />
            <View style={styles.summaryItem}>
              <Text style={styles.summaryLabel}>Profit Per Day</Text>
              <Text style={styles.summaryValue}>₹{formatIndianNumber(dailyProfit)}</Text>
            </View>
          </View>
        </View>
      </LinearGradient>

      <ScrollView 
        style={styles.scrollContent} 
        showsVerticalScrollIndicator={false}
        contentContainerStyle={styles.scrollContentContainer}
      >
        {/* Profit Cards Section */}
        <View style={styles.profitSection}>
          <Text style={styles.sectionTitle}>Profit Overview</Text>
          
          {/* Profit Per Year Card */}
          <View style={styles.profitCard}>
            <LinearGradient
              colors={['#10b981', '#059669']}
              style={styles.profitCardGradient}
              start={{ x: 0, y: 0 }}
              end={{ x: 1, y: 1 }}
            >
              <View style={styles.profitCardIcon}>
                <Ionicons name="calendar" size={24} color="#ffffff" />
              </View>
              <View style={styles.profitCardContent}>
                <Text style={styles.profitCardLabel}>Profit Earned Per Year</Text>
                <Text style={styles.profitCardValue}>₹{formatIndianNumber(yearlyProfit)}</Text>
                <Text style={styles.profitCardSubtext}>Based on {formatIndianNumber(yearlyEnergy)} kWh yearly</Text>
              </View>
            </LinearGradient>
          </View>

          {/* Profit by Product Card */}
          <View style={styles.profitCard}>
            <LinearGradient
              colors={['#6366f1', '#8b5cf6']}
              style={styles.profitCardGradient}
              start={{ x: 0, y: 0 }}
              end={{ x: 1, y: 1 }}
            >
              <View style={styles.profitCardIcon}>
                <Ionicons name="cube" size={24} color="#ffffff" />
              </View>
              <View style={styles.profitCardContent}>
                <Text style={styles.profitCardLabel}>Profit Earned by Product</Text>
                <Text style={styles.profitCardValue}>₹{formatIndianNumber(Math.max(0, productProfit))}</Text>
                <Text style={styles.profitCardSubtext}>Live Yearly - User Input Profit</Text>
              </View>
            </LinearGradient>
          </View>

          {/* User Input Section for Product Profit */}
          <View style={styles.userInputCard}>
            <View style={styles.userInputHeader}>
              <Ionicons name="calculator" size={20} color="#6366f1" />
              <Text style={styles.userInputTitle}>Calculate Product Profit</Text>
            </View>
            
            {!userInputConfirmed ? (
              <View style={styles.userInputRow}>
                <View style={styles.inputWrapper}>
                  <TextInput
                    style={styles.energyInput}
                    placeholder="Enter energy (kWh)"
                    placeholderTextColor="#94a3b8"
                    value={userInputEnergy}
                    onChangeText={setUserInputEnergy}
                    keyboardType="decimal-pad"
                  />
                  <Text style={styles.inputUnit}>kWh</Text>
                </View>
                <TouchableOpacity 
                  style={styles.confirmBtn}
                  onPress={handleConfirmUserInput}
                >
                  <Ionicons name="checkmark" size={20} color="#ffffff" />
                </TouchableOpacity>
              </View>
            ) : (
              <View style={styles.confirmedInputDisplay}>
                <View style={styles.confirmedRow}>
                  <Text style={styles.confirmedLabel}>Your Input Energy:</Text>
                  <Text style={styles.confirmedValue}>{formatIndianNumber(confirmedUserEnergy)} kWh</Text>
                </View>
                <View style={styles.confirmedRow}>
                  <Text style={styles.confirmedLabel}>Input Profit (×₹6):</Text>
                  <Text style={styles.confirmedValue}>₹{formatIndianNumber(userInputProfit)}</Text>
                </View>
                <View style={styles.confirmedRow}>
                  <Text style={styles.confirmedLabel}>Live Yearly Profit:</Text>
                  <Text style={styles.confirmedValue}>₹{formatIndianNumber(yearlyProfit)}</Text>
                </View>
                <View style={[styles.confirmedRow, styles.resultRow]}>
                  <Text style={styles.resultLabel}>Product Profit:</Text>
                  <Text style={styles.resultValue}>₹{formatIndianNumber(Math.max(0, productProfit))}</Text>
                </View>
                <TouchableOpacity 
                  style={styles.resetBtn}
                  onPress={handleResetUserInput}
                >
                  <Ionicons name="refresh" size={16} color="#6366f1" />
                  <Text style={styles.resetBtnText}>Reset</Text>
                </TouchableOpacity>
              </View>
            )}
          </View>
        </View>

        {/* Quick Stats Cards */}
        <View style={styles.statsContainer}>
          {quickStats.map((stat, index) => (
            <TouchableOpacity 
              key={index} 
              style={[styles.statCardContainer, { width: getCardWidth() }]}
              activeOpacity={0.7}
            >
              <View style={[styles.statCard, { backgroundColor: stat.bgColor }]}>
                <View style={styles.statIconContainer}>
                  <LinearGradient
                    colors={stat.gradient}
                    style={styles.statIconGradient}
                    start={{ x: 0, y: 0 }}
                    end={{ x: 1, y: 1 }}
                  >
                    <Ionicons name={stat.icon} size={20} color="#ffffff" />
                  </LinearGradient>
                </View>
                <View style={styles.statTextContainer}>
                  <Text style={styles.statTitle}>{stat.title}</Text>
                  <View style={styles.statValueRow}>
                    <Text style={[styles.statValue, { color: stat.gradient[0] }]}>{stat.value}</Text>
                    <Text style={styles.statUnit}>{stat.unit}</Text>
                  </View>
                </View>
                <View style={styles.statArrow}>
                  <Ionicons name="chevron-forward" size={18} color="#cbd5e1" />
                </View>
              </View>
            </TouchableOpacity>
          ))}
        </View>

        {/* System Status Section */}
        <View style={styles.sectionContainer}>
          <View style={styles.sectionHeader}>
            <Text style={styles.sectionTitle}>System Status</Text>
            <TouchableOpacity style={styles.seeAllButton}>
              <Text style={styles.seeAllText}>See All</Text>
              <Ionicons name="chevron-forward" size={16} color="#6366f1" />
            </TouchableOpacity>
          </View>
          <View style={styles.statusContainer}>
            {systemStatus.map((system, index) => (
              <View 
                key={index} 
                style={[
                  styles.statusItem,
                  index === systemStatus.length - 1 && styles.statusItemLast
                ]}
              >
                <View style={[styles.statusIconContainer, { backgroundColor: system.color + '15' }]}>
                  <Ionicons name={system.icon} size={20} color={system.color} />
                </View>
                <View style={styles.statusInfo}>
                  <Text style={styles.systemName}>{system.name}</Text>
                  <Text style={styles.systemValue}>{system.value}</Text>
                </View>
                <View style={styles.statusBadge}>
                  <View style={styles.statusDot} />
                  <Text style={styles.statusText}>{system.status}</Text>
                </View>
              </View>
            ))}
          </View>
        </View>

        {/* Recent Alerts Section */}
        <View style={styles.sectionContainer}>
          <View style={styles.sectionHeader}>
            <Text style={styles.sectionTitle}>Recent Alerts</Text>
            <View style={styles.alertBadgeCount}>
              <Text style={styles.alertBadgeText}>2</Text>
            </View>
          </View>
          <View style={styles.alertContainer}>
            {alerts.map((alert, index) => (
              <TouchableOpacity 
                key={index} 
                style={[
                  styles.alertItem,
                  index === alerts.length - 1 && styles.alertItemLast
                ]}
                activeOpacity={0.7}
              >
                <View style={[styles.alertIconContainer, { backgroundColor: alert.bgColor }]}>
                  <Ionicons name={alert.icon} size={20} color={alert.color} />
                </View>
                <View style={styles.alertContent}>
                  <Text style={styles.alertText}>{alert.text}</Text>
                  <Text style={styles.alertTime}>{alert.time}</Text>
                </View>
                <Ionicons name="chevron-forward" size={18} color="#cbd5e1" />
              </TouchableOpacity>
            ))}
          </View>
        </View>

        {/* Bottom Spacing */}
        <View style={styles.bottomSpacer} />
      </ScrollView>
    </View>
  );
};

